String.prototype.ltrim=function(t){return t?this.replace(new RegExp("^["+t+"]+"),""):this.replace(/^\s+/,"")},String.prototype.rtrim=function(t){return t?this.replace(new RegExp("["+t+"]+$"),""):this.replace(/\s+$/,"")},String.prototype.trim=function(t){return t?this.ltrim(t).rtrim(t):this.ltrim().rtrim()};
