INSERT INTO `recipes` VALUES (1,'Restart App Mongrel Cluster','Overrides the deploy.restart, deploy.start, and deploy.stop tasks to use Mongrel Cluster instead.\r\n\r\nREQUIRES: server_classes_with_mongrel\r\n','namespace :webistrano do\r\n          namespace :mongrel do\r\n            server_classes_with_mongrel||=[:app]\r\n            [ :stop, :start, :restart ].each do |t|              \r\n              desc \"#{t.to_s.capitalize} mongrel\"\r\n              task t, :roles => server_classes_with_mongrel , :except => { :no_release => true } do\r\n                as = fetch(:runner, \"app\")\r\n                invoke_command \"cd #{current_path}; mongrel_rails cluster::#{t.to_s} -C #{mongrel_config} --clean\", :via => run_method, :as => as\r\n              end\r\n            end\r\n          end\r\n        end','2009-01-28 16:43:24','2009-05-07 13:26:18',7),(2,'Apache Site Config','Apache Controls','namespace :deploy do\r\n  namespace :apache do\r\n    task :copy_site_conf, :roles=>:web do\r\n      invoke_command \"cp #{current_path}/config/environments/#{rails_env}/apache_site.conf /home/gswww/config/apache_sites/#{application}_#{rails_env}.conf\"\r\n      restart\r\n    end    \r\n    task :restart, :roles=>:web do\r\n      invoke_command \"sudo /etc/init.d/apache2 restart\"\r\n    end\r\n  end\r\nend','2009-01-29 13:46:29','2010-12-26 20:40:58',9),(3,'Search Daemon Control','','namespace :search do\r\n#  task :index, :roles=>:search do\r\n#   invoke_command \"cd #{current_path}; RAILS_ENV=#{rails_env} rake thinking_sphinx:index\"\r\n#  end\r\n#  task :restart, :roles=>:search do\r\n#    invoke_command \"cd #{current_path}; RAILS_ENV=#{rails_env} rake thinking_sphinx:running_start\"\r\n#  end\r\n\r\n  desc \"Generate the Sphinx configuration file\"\r\n  task :configure, :roles=>:search do\r\n    rake \"thinking_sphinx:configure\"\r\n  end\r\n  \r\n  desc \"Index data\"\r\n  task :index, :roles=>:search do\r\n    rake \"thinking_sphinx:index\"\r\n  end\r\n  \r\n  desc \"Start the Sphinx daemon\"\r\n  task :start, :roles=>:search do\r\n    configure\r\n    rake \"thinking_sphinx:start\"\r\n  end\r\n  \r\n  desc \"Stop the Sphinx daemon\"\r\n  task :stop, :roles=>:search do\r\n    configure\r\n    rake \"thinking_sphinx:stop\"\r\n  end\r\n  \r\n  desc \"Stop and then start the Sphinx daemon\"\r\n  task :restart, :roles=>:search do\r\n    stop\r\n    start\r\n  end\r\n  \r\n  desc \"Stop, re-index and then start the Sphinx daemon\"\r\n  task :rebuild, :roles=>:search do\r\n    stop\r\n    index\r\n    start\r\n  end\r\n  \r\n  desc \"Add the shared folder for sphinx files for the production environment\"\r\n  task :shared_sphinx_folder, :roles => :search do\r\n    run \"mkdir -p #{shared_path}/db/sphinx/#{rails_env}\"\r\n  end\r\n\r\n  def rake(*tasks)\r\n    tasks.each do |t|\r\n      run \"cd #{current_path} && RAILS_ENV=#{rails_env} rake #{t}\"\r\n    end\r\n  end\r\nend\r\n\r\n# after(\"deploy:update_code\", \"search:configure\")','2009-01-29 14:54:36','2011-06-29 21:20:40',10),(4,'Testing','Application unit and functional tests.','output = lambda do |ssh, stream, data|if stream == :err\r\n    print \"\\nERR:\"\r\n  end\r\n  print data\r\nend\r\n\r\n\r\nnamespace :testing do\r\n  \r\n  desc \"running all tests\"\r\n  task :default do\r\n    print \"RUN ALL TESTS\\n\"\r\n    testing::unit\r\n    testing::functionals\r\n  end\r\n  \r\n  desc \"running unit tests\"\r\n  task :unit, :roles => :app do\r\n      print \"TESTING UNITS...\\n\"\r\n      run \"cd #{current_path}; rake test:units\", &output\r\n  end\r\n  \r\n  desc \"running functional tests\"\r\n  task :functionals, :roles => :app do\r\n    print \"TESTING FUNCTIONALS...\\n\"\r\n    run \"cd #{current_path}; rake test:functionals\", &output\r\n  end\r\nend','2009-02-03 13:02:06','2009-02-03 13:02:06',1),(5,'SVN Tasks','sync work with svn repository\r\n','desc \"Rsync original item images (alpha -> design)\"\r\ntask :rsync_images, :roles=>:app do\r\n  paths=%w{ public/system/originals }\r\n  paths.each do |path|\r\n    invoke_command \"cd #{current_path}; cd ../..; rsync -a alpha/current/#{path}/* #{rails_env}/current/#{path}/\"\r\n  end\r\nend\r\n\r\nnamespace :svn do\r\n  desc \"Run the sync script (current/script/svn_sync_dir.rb)\"\r\n  task :sync, :roles=>:app do\r\n    sync_cmd=\"ruby #{current_path}/script/svn_sync_dir.rb\"\r\n    paths=%w{ public/images public/javascripts public/stylesheets app config/custom }\r\n    paths.each do |path|\r\n      invoke_command \"#{sync_cmd} #{current_path}/#{path}\"\r\n#      rsync_images\r\n    end\r\n  end\r\nend','2009-06-05 12:01:49','2010-04-15 09:51:04',25),(6,'default Deploy overwrite','Overwrites default deploy behavior to create an additional symlink.\r\nLink public/images/offers -> public/system/originals/offers\r\nLink config/custom to shared','namespace :deploy do\r\n  task :migrations do\r\n    set :migrate_target, :latest\r\n    # do the deploy, migrate and symlink\r\n    update_migrate\r\n    restart\r\n  end\r\n\r\n  # Originaly, update_code, migrate and symlink are located in this order \r\n  # in the task deploy:migrations. The code sourced out to deploy:update_migrate\r\n  # creates new tasks for before_update_migrate and after_update_migrate \r\n  # on runtime. These are used in \'Before And After Update\' recipe.\r\n  task :update_migrate do\r\n    transaction do\r\n      update_code\r\n      migrate\r\n      symlink\r\n    end\r\n  end\r\nend','2009-08-05 14:45:17','2010-07-19 12:38:55',22),(8,'Mongrel Cluster','Overrides webistranos deploy tasks','namespace :deploy do\r\n  desc \"print current path\"\r\n  task :print_current_path, :roles => :app do \r\n    run \"echo #{current_path};\"\r\n  end\r\n\r\n  desc \"start all mongrels\"\r\n  task :start, :roles => :app do\r\n    run \"cd #{current_path}; mongrel_rails cluster::start\"\r\n  end\r\n\r\n  desc \"stop all mongrels\"\r\n  task :stop, :roles => :app do\r\n    run \"cd #{current_path}; mongrel_rails cluster::stop\"\r\n  end\r\n\r\n  desc \"restart all mongrels\"\r\n  task :restart, :roles => :app do\r\n    run \"cd #{current_path}; mongrel_rails cluster::restart\"\r\n  end\r\nend','2009-10-19 10:28:49','2009-10-19 11:26:08',10),(12,'Before And After Update','Before the code will be updated the task :before_update_code will be called. \r\nAfter the code is updated the task :after_update_code will be called. ','desc \"Before Update Task\"\r\ntask :before_update do\r\n  create_deploy_pid_file\r\nend\r\n\r\ndesc \"After Update Task\"\r\ntask :after_update do\r\n  update_stage\r\n  delete_deploy_pid_file\r\n#  search.rebuild\r\nend\r\n\r\ndesc \"Before Update Migrate Task\"\r\ntask :before_update_migrate do\r\n  create_deploy_pid_file\r\nend\r\n\r\ndesc \"After Update Migrate Task\"\r\ntask :after_update_migrate do\r\n  update_stage\r\n  delete_deploy_pid_file\r\n#  search.rebuild\r\nend\r\n\r\ndesc \"After Rollback Migrate Task\"\r\ntask :rollback do\r\n  delete_deploy_pid_file\r\nend\r\n\r\ndesc \"Copy public file, create symlinks to shared directories and fill up configs\"\r\ntask :update_stage do\r\n  # copy all public files in shared (e.g. google*.html) \r\n  # into the public of the current stage\r\n  deploy.copy_public_files\r\n    \r\n  # create symlinks to shared\r\n  deploy.create_symlinks_to_shared_directories\r\n\r\n  # check wether to fill up stage configs\r\n  deploy.fill_up_custom_and_background_configs\r\nend\r\n\r\ndesc \"Create the deploy pid file\"\r\ntask :create_deploy_pid_file do\r\n  # deploy pid file prevent job center to start by cronjob\r\n  run \"mkdir -p #{shared_path}/pids\"\r\n  run \"echo \'#{Process.pid}\' > #{shared_path}/pids/deploy.pid\"\r\nend\r\n\r\ndesc \"Delete the deploy pid file\"\r\ntask :delete_deploy_pid_file do\r\n  # deploy pid file prevent jobs to start by cronjob, so delete it now\r\n  run \"rm -rf #{shared_path}/pids/deploy.pid\"\r\nend\r\n','2009-12-18 13:09:17','2011-04-01 14:11:07',39),(14,'Generate Application Documentation','